import React, { useState } from "react";
import starSvg from "../src/asset/icon-star.svg";
import Button from "./Component/UI/Button";

function App() {
  const [initialIndex, setInitialIndex] = useState({ currentindex: -1 });

  const Data = {
    title: "How did we do?",
    paragraph:
      "Please let us know how we did with your support request. All feedback is appreciated to help us improve our offering!",
    rating: [1, 2, 3, 4, 5],
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("form submit");
  };
  const handleClick = (e,index) => {
    console.log("onclick", index);
  };
  return (
    <React.Fragment>
      <div class="card mx-auto mt-3" style={{ width: "20rem" }}>
        <div class="card-body">
          <img alt="starSvg" className="star" src={starSvg} />
          <h5 class="card-title my-2">{Data.title}</h5>
          {/* <h6 class="card-subtitle mb-2 text-muted">Card subtitle</h6> */}
          <p class="card-text">{Data.paragraph}</p>
          <div className="rating-main">
            {Data.rating.map((rating, i) => (
              <Button
                key={rating}
                onClick={handleClick.bind(null,i)}
                className={`rating-Button mx-1 ${
                  initialIndex.currentindex === i ? "active" : "unactive"
                }`}
                type="button">
                {rating}
              </Button>
            ))}
          </div>
          <form onSubmit={handleSubmit}>
            <Button className="submit" type="submit">
              Submit
            </Button>
          </form>
        </div>
      </div>
    </React.Fragment>
  );
}

export default App;
